import React from 'react';
import './App.css';
import Spotify from 'spotify-web-api-js';

const spotifyWebAPI = new Spotify()

class App extends React.Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: 'Not Checked',
        image: '',
      },
      topTrack: undefined
    }
    if (params.access_token) {
      spotifyWebAPI.setAccessToken(params.access_token)
    }
  }

  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while (e = r.exec(q)) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getTopTracks() {
    spotifyWebAPI.getMyTopTracks()
      .then((response) => {
        this.setState({
          topTrack: response.items[0].album.images[1].url
        })
      })
      .then((response) => { console.log(this.state.topTracks) })
  }

  getNowPlaying() {
    spotifyWebAPI.getMyCurrentPlaybackState()
      .then((response) => {
        this.setState({
          nowPlaying: {
            name: response.item.name,
            image: response.item.album.images[0].url
          }
        })
      })
  }

  render() {
    return (
      <div className="App">
        <a href='http://localhost:8888'>
          <button >Login with Spotify</button>
        </a>
        <div> Now Playing:  {this.state.nowPlaying.name} </div>
        <div>
          <img src={this.state.nowPlaying.image} style={{ width: 500 }} />
        </div>
        <button onClick={() => this.getNowPlaying()} >
          Check Now Playing
         </button>

        <button onClick={() => this.getTopTracks()} >
          Get Top Tracks
         </button>
        <TopTracks topTrack={this.state.topTrack} />
      </div>
    );
  }
}

function TopTracks() {

  return (
    <img src={this.props.topTrack} />
  )
}


export default App;
