import React from 'react';
import './App.css';
import Spotify from 'spotify-web-api-js';

const spotifyWebAPI = new Spotify()

class App extends React.Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: 'Not Checked',
        image: undefined,
      },
      topTrack: undefined,
      topTracksArray: undefined
    }

    if (params.access_token) {
      spotifyWebAPI.setAccessToken(params.access_token)
    }
  }

  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while (e = r.exec(q)) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getTopTracks() {
    spotifyWebAPI.getMyTopTracks()
      .then((response) => {
        let topArrayImages = [];
        for (let i = 0; i < 5; i++) {
          topArrayImages.push(response.items[i].album.images[1].url)
        }
        this.setState({
          topTracksArray: topArrayImages.slice(0),
          topTrack: response.items[0].album.images[1].url
        });
        console.log(this.state)
      })

  }

  getNowPlaying() {
    spotifyWebAPI.getMyCurrentPlaybackState()
      .then((response) => {
        this.setState({
          nowPlaying: {
            name: response.item.name,
            image: response.item.album.images[0].url
          }
        })
      })
  }

  render() {
    return (
      <div className="App">

        <NowPlaying nowPlaying={this.state.nowPlaying} />

        <div id="buttonDiv">
          <button onClick={() => this.getNowPlaying()} >
            Check Now Playing
         </button>

          <button onClick={() => this.getTopTracks()} >
            Get Top Tracks
         </button>
          <TopTracks topTrack={this.state.topTrack} />
        </div>
      </div>
    );
  }
}

const TopTracks = (topTrack) => {

  if (topTrack.topTrack) {
    return (

      <div>
        {console.log(topTrack)}
        <img src={topTrack.topTrack} style={{ float: 'left', width: 200 }} />
      </div>
    )
  } else {
    return null
  }
}

const NowPlaying = (nowPlaying) => {

  if (nowPlaying.nowPlaying.image != undefined) {
    return (
      <div id="albumDiv">
        {console.log(nowPlaying)}
        <img src={nowPlaying.nowPlaying.image} style={{ width: '100%' }} />
        <div style={{ padding: 'auto' }}> Now Playing:  {nowPlaying.nowPlaying.name} </div>
      </div>
    )
  } else {
    return null;
  }
}


export default App;
