import React from 'react';
import './App.css';
import Spotify from 'spotify-web-api-js';

const spotifyWebAPI = new Spotify()

class App extends React.Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: 'Not Checked',
        image: undefined,
      },
      topTrack: undefined
    }

    if (params.access_token) {
      spotifyWebAPI.setAccessToken(params.access_token)
    }
  }

  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while (e = r.exec(q)) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getTopTracks() {
    spotifyWebAPI.getMyTopTracks()
      .then((response) => {
        this.setState({
          topTrack: response.items[0].album.images[1].url
        })
      })
      .then((response) => { console.log(this.state.topTracks) })
  }

  getNowPlaying() {
    spotifyWebAPI.getMyCurrentPlaybackState()
      .then((response) => {
        this.setState({
          nowPlaying: {
            name: response.item.name,
            image: response.item.album.images[0].url
          }
        })
      })
  }

  render() {
    return (
      <div className="App">
        <a href='http://localhost:8888'>
          <button >Login with Spotify</button>
        </a>

        <NowPlaying nowPlaying={this.state.nowPlaying.image} />

        <div> Now Playing:  {this.state.nowPlaying.name} </div>
        <button onClick={() => this.getNowPlaying()} >
          Check Now Playing
         </button>

        <button onClick={() => this.getTopTracks()} >
          Get Top Tracks
         </button>
        <TopTracks topTrack={this.state.topTrack} />
      </div>
    );
  }
}

const TopTracks = (topTrack) => {

  return (
    <div>
      {console.log(topTrack)}
      <img src={topTrack.topTrack} style={{ float: 'left', width: 200 }} />
    </div>
  )
}

const NowPlaying = (nowPlaying) => {

  if (nowPlaying) {
    return (
      <div style={{ width: 400, margin: 'auto' }}>
        {console.log(nowPlaying)}
        <img src={nowPlaying.nowPlaying} style={{ width: 400, boxShadow: '10px 10px 5px grey', border: '3px solid #ddd', borderRadius: '8px', padding: '5px' }} />
      </div>
    )
  } else {
    return null;
  }
}


export default App;
